import React from "react";
import { Button, Card, Form, Input, Space, Upload } from "antd";
import { CloseOutlined, InboxOutlined } from "@ant-design/icons";
import { useAddRecipe } from "../../context/AddRecipeContext";

const {
  addRecipeFields,
  saveToLocalStorage,
  loadFromLocalStorage,
  getAllRecepies,
  addRecepie,
} = useAddRecipe();

function AddRecepie() {
  const [form] = Form.useForm();
  const normFile = (e) => {
    console.log("Upload event:", e);
    if (Array.isArray(e)) {
      return e;
    }
    return e?.fileList;
  };

  function onSubmit(data) {
    console.log(data);
    let recepie = { ...data };
    recepie.ingredients.map((item) => item.first);
    addRecepie(recepie);
    console.log(getAllRecepies());
  }
  return (
    <div>
      <Form
        form={form}
        name="validateOnly"
        layout="vertical"
        requiredMark={false}
        onFinish={onSubmit}
      >
        <Form.Item
          name="title"
          label="Recepie Title"
          rules={[{ required: true }]}
        >
          <Input placeholder="Enter Your Recipe name" />
        </Form.Item>
        <Form.Item label="Recepie Image">
          <Form.Item
            valuePropName="fileList"
            getValueFromEvent={normFile}
            noStyle
          >
            <Upload.Dragger name="files" action="/upload.do" multiple type="pi">
              <p className="ant-upload-drag-icon">
                <InboxOutlined />
              </p>
              <p className="ant-upload-text">
                Click or drag file to this area to upload
              </p>
              <p className="ant-upload-hint">
                Support for a single or bulk upload.
              </p>
            </Upload.Dragger>
          </Form.Item>
        </Form.Item>

        <Form.Item label="Description" name="description">
          <Input.TextArea allowClear showCount />
        </Form.Item>

        <Form.Item label="Ingredients">
          <Form.List name="ingredients">
            {(subFields, subOpt) => (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  rowGap: 16,
                }}
              >
                {subFields.map((subField) => (
                  <Space key={subField.key}>
                    <Form.Item noStyle name={[subField.name, "first"]}>
                      <Input placeholder="Write Instructions" />
                    </Form.Item>
                    <CloseOutlined
                      onClick={() => {
                        subOpt.remove(subField.name);
                      }}
                    />
                  </Space>
                ))}
                <Button type="dashed" onClick={() => subOpt.add()}>
                  +
                </Button>
              </div>
            )}
          </Form.List>
        </Form.Item>
        <Form.Item name="serving" label="Serving" rules={[{ required: true }]}>
          <Input />
        </Form.Item>
        <Form.Item>
          <Space>
            <Button htmlType="submit">Submit</Button>
          </Space>
        </Form.Item>
      </Form>
    </div>
  );
}

export default UploadButton;
