// CartContext.js

import React, { createContext, useContext, useState } from "react";

const RecipeContext = createContext();

export const RecipeProvider = ({ children }) => {
  const [data, setData] = useState([
    {
      id: 1,
      name: "Onion Rings",
      username: "Alex Martin",
      image:
        "https://i.ibb.co/Fs5k7RD/s-homepage-recipe-row-36700-erin-me-1384569-a-93-f-4-fde-a-6-ce-3-e-21-b-69-b-04-fa-1.png",
      userimage: "https://i.ibb.co/yhrMpQz/s-homepage-recipe-row-user-icon.png",
    },
    {
      id: 1,
      name: "Italian Pastry",
      username: "Aasil Ghoury",
      image:
        "https://i.ibb.co/HCgKfxk/f.png",
      userimage: "https://i.ibb.co/yhrMpQz/s-homepage-recipe-row-user-icon.png",
    },
    {
      id: 1,
      name: "Chicken Rolls",
      username: "Zaki Ghoury",
      image:
        "https://i.ibb.co/r4kLgx3/c.png",
      userimage: "https://i.ibb.co/yhrMpQz/s-homepage-recipe-row-user-icon.png",
    },
    {
      id: 1,
      name: "O Rings",
      username: "Ali Usman",
      image:
        "https://i.ibb.co/0KBZMGF/a.png",
      userimage: "https://i.ibb.co/yhrMpQz/s-homepage-recipe-row-user-icon.png",
    },
    {
      id: 1,
      name: "Onion Rings",
      username: "Alex Martin",
      image:
        "https://i.ibb.co/Fs5k7RD/s-homepage-recipe-row-36700-erin-me-1384569-a-93-f-4-fde-a-6-ce-3-e-21-b-69-b-04-fa-1.png",
      userimage: "https://i.ibb.co/yhrMpQz/s-homepage-recipe-row-user-icon.png",
    },
    {
      id: 1,
      name: "Onion Rings",
      username: "Alex Martin",
      image:
        "https://i.ibb.co/Fs5k7RD/s-homepage-recipe-row-36700-erin-me-1384569-a-93-f-4-fde-a-6-ce-3-e-21-b-69-b-04-fa-1.png",
      userimage: "https://i.ibb.co/yhrMpQz/s-homepage-recipe-row-user-icon.png",
    },
  ]);

  return (
    <RecipeContext.Provider value={{ data, setData }}>
      {children}
    </RecipeContext.Provider>
  );
};

export const useRecipe = () => {
  const context = useContext(RecipeContext);
  if (!context) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
};
