import React from "react";
import "./AddRecipe.scss";
import {
  Form,
  Input,
  Button,
  Upload,
  Select,
  Space,
  InputNumber,
  Cascader,
  List,
  Divider,
  Icon,
} from "antd";
import { UploadOutlined } from "@ant-design/icons";

import { useAddRecipe } from "../../context/AddRecipeContext";

function AddRecipe() {
  const { addRecipeFields } = useAddRecipe();
  console.log(addRecipeFields);
  return (
    <div>
      <>
        <Form
          form={form}
          onFinish={handleSubmit}
          layout="vertical"
          initialValues={JSON.parse(localStorage.getItem("recipeData")) || {}}
        >
          {/* Recipe Title Field */}
          <Form.Item
            label="Recipe Title"
            name="title"
            rules={[
              { required: true, message: "Please input the recipe title" },
            ]}
          >
            <Input />
          </Form.Item>

          {/* Recipe Description Field */}
          <Form.Item label="Recipe Description" name="description">
            <Input.TextArea />
          </Form.Item>

          {/* Recipe Image Upload Button */}
          <Form.Item>
            <Upload {...uploadProps}>
              <Button icon={<UploadOutlined />}>Upload Recipe Image</Button>
            </Upload>
          </Form.Item>

          {/* Ingredients Dynamic Input */}
          <Form.List name="ingredients">
            {(fields, { add, remove }) => (
              <div>
                {fields.map((field) => (
                  <Space
                    key={field.key}
                    style={{ display: "flex", flexWrap: "wrap" }}
                  >
                    <Form.Item
                      {...field}
                      rules={[
                        { required: true, message: "Please input ingredient" },
                      ]}
                    >
                      <Input placeholder="Ingredient" />
                    </Form.Item>
                    <Icon
                      type="minus-circle-o"
                      onClick={() => remove(field.name)}
                    />
                  </Space>
                ))}
                <Button
                  type="dashed"
                  onClick={() => add()}
                  style={{ width: "100%" }}
                >
                  Add Ingredient
                </Button>
              </div>
            )}
          </Form.List>

          {/* Instructions Dynamic Input */}
          {/* ... similar structure as Ingredients Dynamic Input */}

          {/* Serving Field */}
          <Form.Item label="Servings" name="servings">
            <InputNumber min={1} />
          </Form.Item>

          {/* Cooking Time Field */}
          <Form.Item label="Cooking Time (minutes)" name="cookingTime">
            <InputNumber min={0} />
          </Form.Item>

          {/* Cuisine Type Dropdown */}
          <Form.Item
            label="Cuisine Type"
            name="cuisineType"
            rules={[
              { required: true, message: "Please select a cuisine type" },
            ]}
          >
            <Select>
              <Select.Option value="Italian">Italian</Select.Option>
              <Select.Option value="Chinese">Chinese</Select.Option>
              <Select.Option value="Thai">Thai</Select.Option>
              {/* ... other cuisine options */}
            </Select>
          </Form.Item>
        </Form>
      </>
    </div>
  );
}

export default AddRecipe;
