import React, { useState } from "react";
import { Select, Form, Input, Button, Upload, message } from "antd";
import { useNavigate } from "react-router-dom";
import { UploadOutlined, LoadingOutlined } from "@ant-design/icons";
const { Option } = Select;
const navigate = useNavigate();
const [loading, setLoading] = useState(false);
const [imageUrl, setImageUrl] = useState(null);

function loadFromLocalStorage(key) {
  try {
    const item = localStorage.getItem(key);
    if (!item) {
      throw new Error("Item not found in local storage");
    }
    return JSON.parse(item);
  } catch (error) {
    console.error("Error retrieving item from local storage:", error);
  }
}
function saveToLocalStorage(key, value) {
  try {
    localStorage.setItem(key, JSON.stringify(value));
  } catch (error) {
    console.error("Error saving item to local storage:", error);
  }
}
function getAllRecepies() {
  return loadFromLocalStorage("recepies") || "[]";
}
function addRecepie(recepie) {
  let recepies = getAllRecepies();
  recepies.push(recepie);
  saveToLocalStorage("recepies", recepies);
}

function RecipeForm() {
  const [form] = Form.useForm();
  const normFile = (e) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };
  const onFinish = (e) => {
    const allRecipies = getAllRecepies();
    const updatedRecipes = [...allRecipies, { ...e }];
    saveToLocalStorage("recepies", updatedRecipes);
    // navigate("/recipe-Listing");
  };
  const getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
  };

  const beforeUpload = (e) => {
    const isImage = e.type.startsWith("image/");
    if (!isImage) {
      message.error("You can only upload image files!");
    }
    return isImage;
  };
  const handleChange = (e) => {
    if (e.file.status === "uploading") {
      setLoading(true);
      return;
    }
    if (e.file.status === "done") {
      getBase64(e.file.originFileObj, (url) => {
        setLoading(false);
        setImageUrl(url);
      });
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <UploadOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  function onSubmit(data) {
    console.log(data);
    let recepie = { ...data };
    recepie.ingredients.map((item) => item.first);
    addRecepie(recepie);

    console.log(getAllRecepies());
  }

  return (
    <div>
      <Form
        form={form}
        name="addRecipeForm"
        onFinish={onFinish}
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
      >
        <Form.Item
          label="Title"
          name="title"
          className="recipe-title"
          rules={[{ required: true, message: "Please input the title!" }]}
        >
          <Input placeholder="Title" />
        </Form.Item>
        <Form.Item
          label="Recipe Image"
          name="image"
          className="upload"
          valuePropName="fileList"
          getValueFromEvent={normFile}
          noStyle
        >
          <Upload
            name="avatar"
            listType="picture-card"
            className="avatar-uploader"
            showUploadList={false}
            action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
            beforeUpload={beforeUpload}
            onChange={handleChange}
          >
            {imageUrl ? (
              <img
                src={imageUrl}
                alt="avatar"
                style={{
                  width: "-webkit-fill-available",
                  height: "200px",
                  objectFit: "fill",
                }}
              />
            ) : (
              uploadButton
            )}
          </Upload>
        </Form.Item>
        <Form.Item
          label="Description"
          name="description"
          rules={[{ required: true, message: "Please input the description!" }]}
        >
          <Input placeholder="Description" />
        </Form.Item>

        <Form.Item
          label="Ingredients"
          name="ingredients"
          rules={[{ required: true, message: "Please input the ingredients!" }]}
        >
          <Input.TextArea placeholder="Ingredients" />
        </Form.Item>

        <Form.Item
          label="Directions"
          name="directions"
          rules={[{ required: true, message: "Please input the directions!" }]}
        >
          <Input.TextArea placeholder="Directions" />
        </Form.Item>

        <Form.Item
          label="Serving"
          name="serving"
          rules={[{ required: true, message: "Please input the serving!" }]}
        >
          <Input placeholder="Serving" />
        </Form.Item>

        <Form.Item
          label="Cooking Time"
          name="cooking"
          rules={[
            { required: true, message: "Please input the cooking time!" },
          ]}
        >
          <Input placeholder="Cooking Time" />
        </Form.Item>

        <Form.Item
          label="Prep Time"
          name="prep"
          rules={[{ required: true, message: "Please input the prep time!" }]}
        >
          <InputNum placeholder="Prep Time" />
        </Form.Item>

        <Form.Item
          label="Cuisine"
          name="cuisine"
          rules={[{ required: true, message: "Please select the cuisine!" }]}
        >
          <Select defaultValue="Select Cuisine" style={{ width: 200 }}>
            <Option value="Thai">Thai</Option>
            <Option value="Chinese">Chinese</Option>
            <Option value="Arabian">Arabian</Option>
          </Select>
        </Form.Item>

        <Form.Item
          label="Collection"
          name="collection"
          rules={[{ required: true, message: "Please select the collection!" }]}
        >
          <Select defaultValue="Select Collection" style={{ width: 200 }}>
            <Option value="New Collection">New Collection</Option>
            <Option value="Cook Book">Cook Book</Option>
            <Option value="My Recipe">My Recipe</Option>
          </Select>
        </Form.Item>
        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}

export default RecipeForm;
