import React, { useState } from "react";
import { Button, Form, Input, Space, Upload, Modal } from "antd";
import { CloseOutlined, PlusOutlined } from "@ant-design/icons";

const [previewOpen, setPreviewOpen] = useState(false);
const [previewImage, setPreviewImage] = useState("");
const [previewTitle, setPreviewTitle] = useState("");
const [fileList, setFileList] = useState([
  {
    uid: "-1",
    name: "image.png",
    status: "done",
    url: "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
  },
]);
function addItem(key, value) {
  localStorage.setItem(key, value);
}
function getItem(key) {
  return localStorage.getItem(key);
}
function getAllRecepies() {
  let recepies = getItem("recepies") || "[]";
  recepies = JSON.parse(recepies);
  return recepies;
}
function addRecepie(recepie) {
  let recepies = getAllRecepies();
  recepies.push(recepie);
  addItem("recepies", JSON.stringify(recepies));
}
function UploadButton() {
  const [form] = Form.useForm();
  function onSubmit(data) {

    let recepie = { ...data };
    recepie.ingredients.map((item) => item.first);
    addRecepie(recepie);
    console.log(getAllRecepies());
  }
  const handleCancel = () => setPreviewOpen(false);
  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewOpen(true);
    setPreviewTitle(
      file.name || file.url.substring(file.url.lastIndexOf("/") + 1)
    );
  };
  const handleChange = ({ fileList: newFileList }) => setFileList(newFileList);
  const uploadButton = (
    <button
      style={{
        border: 0,
        background: "none",
      }}
      type="button"
    >
      <PlusOutlined />
      <div
        style={{
          marginTop: 8,
        }}
      >
        Upload
      </div>
    </button>
  );
  return (
    <div>
      <Form
        form={form}
        name="validateOnly"
        layout="vertical"
        requiredMark={false}
        onFinish={onSubmit}
      >
        <Form.Item
          name="title"
          label="Recepie Title"
          rules={[{ required: true }]}
        >
          <Input placeholder="Enter Your Recipe name" />
        </Form.Item>
        <Form.Item label="Recepie Image">
          <Form.Item
            valuePropName="fileList"
            getValueFromEvent={normFile}
            noStyle
          >
            <Upload
              action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
              listType="picture-card"
              fileList={fileList}
              onPreview={handlePreview}
              onChange={handleChange}
              name="file"
            >
              {fileList.length >= 8 ? null : uploadButton}
            </Upload>
            <Modal
              open={previewOpen}
              title={previewTitle}
              footer={null}
              onCancel={handleCancel}
            >
              <img
                alt="example"
                style={{
                  width: "100%",
                }}
                src={previewImage}
              />
            </Modal>
          </Form.Item>
        </Form.Item>

        <Form.Item label="Description" name="description">
          <Input.TextArea allowClear showCount />
        </Form.Item>

        <Form.Item label="Ingredients">
          <Form.List name="ingredients">
            {(subFields, subOpt) => (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  rowGap: 16,
                }}
              >
                {subFields.map((subField) => (
                  <Space key={subField.key}>
                    <Form.Item noStyle name={[subField.name, "first"]}>
                      <Input placeholder="Write Instructions" />
                    </Form.Item>
                    <CloseOutlined
                      onClick={() => {
                        subOpt.remove(subField.name);
                      }}
                    />
                  </Space>
                ))}
                <Button type="dashed" onClick={() => subOpt.add()}>
                  +
                </Button>
              </div>
            )}
          </Form.List>
        </Form.Item>
        <Form.Item name="serving" label="Serving" rules={[{ required: true }]}>
          <Input />
        </Form.Item>
        <Form.Item>
          <Space>
            {/* <SubmitButton form={form} /> */}
            <Button htmlType="submit">Submit</Button>
          </Space>
        </Form.Item>
      </Form>
    </div>
  );
}

export default UploadButton;
