import React from "react";
import { Select, Form, Input, Button, Upload, InputNumber, Space } from "antd";
import { useAddRecipe } from "../../context/AddRecipeContext";
import "./RecipeForm.scss";
import { CloseOutlined, MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

function RecipeForm() {
  const {
    normFile,
    onFinish,
    beforeUpload,
    handleChange,
    uploadButton,
    form,
    imageUrl,
    validateMessages,
  } = useAddRecipe();

  return (
    <div>
      <Form
        style={{
          maxWidth: 600,
        }}
        validateMessages={validateMessages}
        className="recipe-form"
        form={form}
        name="addRecipeForm"
        onFinish={onFinish}
        layout="vertical"
        scrollToFirstError={true}
        initialValues={{
          cookingTime: 0,
          prepTime: 0,
          servings: 0,
          cuisine: "Select Collection",
        }}
      >
        {/*Input For Recipe Title */}
        <Form.Item
          label="Recipe Title:"
          name="title"
          className="recipe-title"
          colon={true}
          rules={[
            { required: true, message: "Please input the Recipe Title!" },
          ]}
        >
          <Input placeholder="Title" className="antd-form-input" />
        </Form.Item>

        {/*Input For Recipe Image */}
        <Form.Item
          label="Recipe image:"
          name="image"
          getValueFromEvent={normFile}
          valuePropName="fileList"
          noStyle
          className="upload"
        >
          <Upload
            name="avatar"
            accept="image/png, image/jpeg"
            listType="picture"
            className="avatar-uploader"
            showUploadList={false}
            action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
            beforeUpload={beforeUpload}
            onChange={handleChange}
          >
            {imageUrl ? (
              <img
                src={imageUrl}
                alt="avatar"
                style={{
                  height: "200px",
                  objectFit: "fill",
                }}
              />
            ) : (
              uploadButton
            )}
          </Upload>
        </Form.Item>

        {/*Input For Recipe Description */}
        <Form.Item
          label="Description:"
          name="description"
          rules={[
            { required: true, message: "Please input the Recipe Description!" },
          ]}
        >
          {/* <Input placeholder="Description" className="antd-form-input" /> */}
          <Input.TextArea
            showCount
            maxLength={100}
            placeholder="Description"
            className="antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe Ingredientss */}
        <Form.Item
          label="Ingredients:"
          name="ingredients"
          rules={[
            { required: true, message: "Please input the Recipe Ingredients!" },
          ]}
        >
          <Form.List name="ingredients">
            {(fields, { add, remove }) => (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  rowGap: 16,
                }}
              >
                {fields.map(({ key, name, ...restField }) => (
                  <Space
                    key={key}
                    style={{
                      display: "flex",
                      marginBottom: 8,
                    }}
                    align="baseline"
                  >
                    <Form.Item
                      {...restField}
                      name={[name, "ingredient"]}
                      rules={[
                        {
                          required: true,
                          message: "Missing ingredient Name",
                        },
                      ]}
                    >
                      <Input placeholder="Write Instructions" />
                    </Form.Item>
                    <MinusCircleOutlined onClick={() => remove(name)} />
                    {/* <CloseOutlined
                      onClick={() => {
                        subOpt.remove(subField.name);
                      }}
                    /> */}
                  </Space>
                ))}
                {/* <Button
                  // className="ant-upload-select"
                  onClick={() => subOpt.add()}
                >
                  + Header
                </Button> */}
                <Form.Item>
                  <Button
                    type="dashed"
                    onClick={() => add()}
                    block
                    icon={<PlusOutlined />}
                  >
                    Add field
                  </Button>
                </Form.Item>
              </div>
            )}
          </Form.List>
        </Form.Item>

        {/*Input For Recipe Instructions */}
        <Form.Item
          label="Instructions:"
          name="directions"
          rules={[
            { required: true, message: "Please input the Recipe Directions!" },
          ]}
        >
          <Input.TextArea
            placeholder="Directions"
            className="antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe Servings*/}
        <Form.Item
          label="Servings:"
          name="serving"
          rules={[
            { required: true, message: "Please input the Recipe Serving!" },
          ]}
        >
          <Input placeholder="Serving" className="antd-form-input" />
        </Form.Item>

        {/*Input For Recipe Cooking Time*/}
        <Form.Item
          label="Cooking Time:"
          name="cooking"
          className="cooking-time"
          rules={[
            {
              required: true,
              message: "Please input the Recipe Cooking time!",
            },
          ]}
        >
          <InputNumber
            placeholder="Cooking Time"
            className="cooking-time-input antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe Prep Time*/}
        <Form.Item
          label="Prep Time:"
          name="prep"
          className="prep-time"
          rules={[
            {
              required: true,
              message: "Please input the Recipe Preparation time!",
            },
          ]}
        >
          <InputNumber
            placeholder="Prep Time"
            className="prep-time-input antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe cuisine*/}
        <Form.Item
          label="Cuisine:"
          name="cuisine"
          rules={[{ required: true, message: "Please select the cuisine!" }]}
        >
          <Select
            placeholder="Italian"
            style={{ width: 200 }}
            className="antd-form-input"
          >
            <Select.Option value="Thai">Thai</Select.Option>
            <Select.Option value="Chinese">Chinese</Select.Option>
            <Select.Option value="Italian">Italian</Select.Option>
          </Select>
        </Form.Item>

        {/*Input For Recipe Collection*/}
        <Form.Item
          label="Collection:"
          name="collection"
          rules={[
            { required: true, message: "Please select the Recipe Collection!" },
          ]}
        >
          <Select
            placeholder="Select Collection"
            style={{ width: 200 }}
            className="antd-form-input"
          >
            <Select.Option value="New Collection">New Collection</Select.Option>
            <Select.Option value="Cook Book">Cook Book</Select.Option>
            <Select.Option value="My Recipe">My Recipe</Select.Option>
          </Select>
        </Form.Item>

        {/*Input For Recipe Nutrition Facts*/}
        <Form.Item label="Nutrition Facts:" name="facts">
          <Input placeholder="Nutrition Facts" className="antd-form-input" />
        </Form.Item>

        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}

export default RecipeForm;
