import React from "react";
import {
  Select,
  Form,
  Input,
  Button,
  Upload,
  message,
  InputNumber,
} from "antd";
import { useAddRecipe } from "../../context/AddRecipeContext";
import "./RecipeForm.scss";
import { InboxOutlined } from "@ant-design/icons";
const { Dragger } = Upload;

const props = {
  name: "file",
  multiple: true,
  action: "https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188",
  onChange(info) {
    const { status } = info.file;
    if (status !== "uploading") {
      console.log(info.file, info.fileList);
    }
    if (status === "done") {
      message.success(`${info.file.name} file uploaded successfully.`);
    } else if (status === "error") {
      message.error(`${info.file.name} file upload failed.`);
    }
  },
  onDrop(e) {
    console.log("Dropped files", e.dataTransfer.files);
  },
};

function RecipeForm() {
  const {
    normFile,
    onFinish,
    beforeUpload,
    handleChange,
    uploadButton,
    form,
    imageUrl,
    validateMessages,
  } = useAddRecipe();

  return (
    <div>
      <Form
        style={{
          maxWidth: 1000,
        }}
        validateMessages={validateMessages}
        className="recipe-form"
        form={form}
        name="addRecipeForm"
        onFinish={onFinish}
        layout="vertical"
        wrapperCol={{ span: 16 }}
        scrollToFirstError={true}
        size="large"
      >
        <Form.Item
          label="Recipe Title:"
          name="title"
          className="recipe-title"
          colon={true}
          rules={[{ required: true, message: "Please input the title!" }]}
        >
          <Input placeholder="Title" />
        </Form.Item>

        <Form.Item
          label="Recipe image:"
          name="image"
          className="upload"
          valuePropName="fileList"
          getValueFromEvent={normFile}
          noStyle
        >
          <div style={{ width: "100%", textAlign: "center" }}>
            <h1>Upload Your Recipe Image</h1>

            <Upload
              name="avatar"
              accept="image/png, image/jpeg"
              listType="picture"
              className="avatar-uploader"
              showUploadList={false}
              action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
              beforeUpload={beforeUpload}
              onChange={handleChange}
            >
              {imageUrl ? (
                <img
                  src={imageUrl}
                  alt="avatar"
                  style={{
                    height: "200px",
                    objectFit: "fill",
                  }}
                />
              ) : (
                uploadButton
              )}
            </Upload>
          </div>
          {/* <Dragger {...props}>
          <p className="ant-upload-text">Upload Your Recipe Image</p>

            <p className="ant-upload-drag-icon">
              <InboxOutlined />
            </p>
            <p className="ant-upload-hint">
              Max file size 20 MB I Supports: JPG, PNG, webp
            </p>
          </Dragger> */}
        </Form.Item>

        <Form.Item
          label="Description:"
          name="description"
          rules={[{ required: true, message: "Please input the description!" }]}
        >
          <Input placeholder="Description" />
        </Form.Item>

        <Form.Item
          label="Ingredients:"
          name="ingredients"
          rules={[{ required: true, message: "Please input the ingredients!" }]}
        >
          <Input.TextArea placeholder="Ingredients" />
        </Form.Item>

        <Form.Item
          label="Instructions:"
          name="directions"
          rules={[{ required: true, message: "Please input the directions!" }]}
        >
          <Input.TextArea placeholder="Directions" />
        </Form.Item>

        <Form.Item
          label="Servings:"
          name="serving"
          rules={[{ required: true, message: "Please input the serving!" }]}
        >
          <Input placeholder="Serving" />
        </Form.Item>

        <Form.Item
          label="Cooking Time:"
          name="cooking"
          rules={[
            { required: true, message: "Please input the cooking time!" },
          ]}
        >
          <InputNumber placeholder="Cooking Time" />
        </Form.Item>

        <Form.Item
          label="Prep Time:"
          name="prep"
          rules={[{ required: true, message: "Please input the prep time!" }]}
        >
          <InputNumber placeholder="Prep Time" />
        </Form.Item>

        <Form.Item
          label="Cuisine:"
          name="cuisine"
          rules={[{ required: true, message: "Please select the cuisine!" }]}
        >
          <Select defaultValue="Select Cuisine" style={{ width: 200 }}>
            <Select.Option value="Thai">Thai</Select.Option>
            <Select.Option value="Chinese">Chinese</Select.Option>
            <Select.Option value="Arabian">Arabian</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item
          label="Collection:"
          name="collection"
          rules={[{ required: true, message: "Please select the collection!" }]}
        >
          <Select defaultValue="Select Collection" style={{ width: 200 }}>
            <Select.Option value="New Collection">New Collection</Select.Option>
            <Select.Option value="Cook Book">Cook Book</Select.Option>
            <Select.Option value="My Recipe">My Recipe</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Nutrition Facts:" name="facts"></Form.Item>

        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}

export default RecipeForm;
