import React from "react";
import "./AddRecipe.scss";
import { UploadOutlined } from '@ant-design/icons';
import { Button, Form, Input, message, Upload } from 'antd';
import { useAddRecipe } from "../../context/AddRecipeContext";

const props = {
  name: "file",
  action: "https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188",
  headers: {
    authorization: "authorization-text",
  },
  onChange(info) {
    if (info.file.status !== "uploading") {
      console.log(info.file, info.fileList);
    }
    if (info.file.status === "done") {
      message.success(`${info.file.name} file uploaded successfully`);
    } else if (info.file.status === "error") {
      message.error(`${info.file.name} file upload failed.`);
    }
  },
  progress: {
    strokeColor: {
      "0%": "#108ee9",
      "100%": "#87d068",
    },
    strokeWidth: 3,
    format: (percent) => percent && `${parseFloat(percent.toFixed(2))}%`,
  },
};

const normFile = (e) => {
  if (Array.isArray(e)) {
    return e;
  }
  return e?.fileList;
};
function AddRecipe() {
  const { addRecipeFields } = useAddRecipe();
  console.log(addRecipeFields);
  return (
    <div>
      <>
        <Form
          labelCol={{
            span: 4,
          }}
          wrapperCol={{
            span: 14,
          }}
          layout="vertical"
          style={{
            maxWidth: 700,
            margin: "auto",
          }}
        >
          <Form.Item label={addRecipeFields.recipetitle}>
            <Input placeholder="Enter Your Recipe name" />
          </Form.Item>
          <Form.Item
            label={addRecipeFields.recipeimage}
            valuePropName="fileList"
            getValueFromEvent={normFile}
          >
            <Upload {...props}>
              <Button icon={<UploadOutlined />}>Click to Upload</Button>
            </Upload>
          </Form.Item>

          <Form.Item label={addRecipeFields.description}>
            <Input placeholder="Introduce your recipe" />
          </Form.Item>
        </Form>
      </>
    </div>
  );
}

export default AddRecipe;
