import { createContext, useContext, useState } from "react";

const AddRecipeContext = createContext();

export const AddRecipeProvider = ({ children }) => {
  // const [data, setData] = useState([]);
  const [addRecipeFields, setAddRecipeFields] = useState({
    recipeimage: "Recipe image:",
    recipetitle: "Recipe Title:",
    description: "Description:",
    ingredients: "Ingredients:",
    instructions: "Instructions:",
    Servings: "Servings:",
    Cookingtime: "Cooking Time:",
    Preptime: "Prep Time:",
    cuisine: "cuisine:",
    Nutritionfacts: "Nutrition Facts:",
    Collection: "Collection:",
  });

  function saveToLocalStorage(key, value) {
    localStorage.setItem(key, value);
  }

  function loadFromLocalStorage(key) {
    return localStorage.getItem(key);
  }

  function getAllRecepies() {
    let recepies = getItem("recepies") || "[]";
    recepies = JSON.parse(recepies);
    return recepies;
  }

  function addRecepie(recepie) {
    let recepies = getAllRecepies();
    recepies.push(recepie);
    saveToLocalStorage("recepies", JSON.stringify(recepies));
  }

  return (
    <AddRecipeContext.Provider value={{ addRecipeFields ,saveToLocalStorage, loadFromLocalStorage, getAllRecepies, addRecepie }}>
      {children}
    </AddRecipeContext.Provider>
  );
};

export const useAddRecipe = () => {
  const context = useContext(AddRecipeContext);
  if (!context) {
    throw new Error("useAddRecipe must be used within a AddRecipeProvider");
  }
  return context;
};
