import React, { useState } from "react";
import "./AddRecipe.scss";
import { PlusOutlined } from "@ant-design/icons";
import {
  Button,
  Cascader,
  Checkbox,
  ColorPicker,
  DatePicker,
  Form,
  Input,
  InputNumber,
  Radio,
  Select,
  Slider,
  Switch,
  TreeSelect,
  Upload,
} from "antd";
import { useAddRecipe } from "../../context/AddRecipeContext";

const { RangePicker } = DatePicker;
const { TextArea } = Input;
const normFile = (e) => {
  if (Array.isArray(e)) {
    return e;
  }
  return e?.fileList;
};
function AddRecipe() {
  const { addRecipeFields } = useAddRecipe();
  console.log(addRecipeFields);
  return (
    <div>
      <>
        <Form
          labelCol={{
            span: 4,
          }}
          wrapperCol={{
            span: 14,
          }}
          layout="vertical"
          style={{
            maxWidth: 700,
            margin: "auto",
          }}
        >
          <Form.Item label={addRecipeFields.recipetitle}>
            <Input placeholder="Enter Your Recipe name" />
          </Form.Item>
          <Form.Item
            label={addRecipeFields.recipeimage}
            valuePropName="fileList"
            getValueFromEvent={normFile}
          >
            <Upload action="/upload.do" listType="picture-card">
              <button
                style={{
                  border: 0,
                  background: "none",
                }}
                type="button"
              >
                <PlusOutlined />
                <div
                  style={{
                    marginTop: 8,
                  }}
                >
                  Upload
                </div>
              </button>
            </Upload>
            <Upload name="logo" action="/upload.do" listType="picture">
        <Button icon={<UploadOutlined />}>Click to upload</Button>
      </Upload>
          </Form.Item>

          <Form.Item label={addRecipeFields.description}>
            <Input placeholder="Introduce your recipe"/>
          </Form.Item>


        </Form>
      </>
    </div>
  );
}

export default AddRecipe;
