import React from "react";
import { Select, Form, Input, Button, Upload, InputNumber, Space } from "antd";
import { useAddRecipe } from "../../context/AddRecipeContext";
import "./RecipeForm.scss";
import {
  CloseOutlined,
  MinusCircleOutlined,
  PlusOutlined,
} from "@ant-design/icons";

function RecipeForm() {
  const {
    normFile,
    onFinish,
    beforeUpload,
    handleChange,
    uploadButton,
    form,
    imageUrl,
  } = useAddRecipe();

  const [gonext, setGonext] = <React className="useState"></React>(false);

  return (
    <div>
      <Form
        style={{
          maxWidth: 700,
        }}
        className="recipe-form"
        form={form}
        name="addRecipeForm"
        onFinish={onFinish}
        layout="vertical"
        scrollToFirstError={true}
        initialValues={{
          cookingTime: 0,
          prepTime: 0,
          servings: 0,
          cuisine: "Select Collection",
        }}
      >
        {/*Input For Recipe Title */}
        <Form.Item
          label="Recipe Title:"
          name="title"
          className="recipe-title"
          colon={true}
          rules={[
            { required: true, message: "Please input the Recipe Title!" },
          ]}
        >
          <Input
            placeholder="Enter Your Recipe name"
            className="antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe Image */}
        <Form.Item
          label="Recipe image:"
          name="image"
          getValueFromEvent={normFile}
          valuePropName="fileList"
          noStyle
          className="upload"
        >
          <Upload
            name="avatar"
            accept="image/png, image/jpeg"
            listType="picture"
            className="avatar-uploader"
            showUploadList={false}
            action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
            beforeUpload={beforeUpload}
            onChange={handleChange}
          >
            {imageUrl ? (
              <img
                src={imageUrl}
                alt="avatar"
                style={{
                  height: "200px",
                  objectFit: "fill",
                }}
              />
            ) : (
              uploadButton
            )}
          </Upload>
        </Form.Item>

        {/*Input For Recipe Description */}
        <Form.Item
          label="Description:"
          name="description"
          rules={[
            { required: true, message: "Please input the Recipe Description!" },
          ]}
        >
          {/* <Input placeholder="Description" className="antd-form-input" /> */}
          <Input.TextArea
            showCount
            maxLength={100}
            placeholder="Introduce your recipe"
            className="antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe Ingredientss */}
        <Form.Item
          label="Ingredients:"
          name="ingredients"
          rules={[
            { required: true, message: "Please input the Recipe Ingredients!" },
          ]}
        >
          <Form.List name="ingredients">
            {(fields, { add, remove }) => (
              <>
                {fields.map(({ key, name, ...restField }) => (
                  <Space
                    key={key}
                    style={{
                      display: "flex",
                      marginBottom: 8,
                    }}
                    align="baseline"
                  >
                    <Form.Item
                      {...restField}
                      name={[name, "ingredient"]}
                      rules={[
                        {
                          required: true,
                          message: "Missing Ingredient name",
                        },
                      ]}
                    >
                      <Input placeholder="Add ingredients" />
                    </Form.Item>
                    <MinusCircleOutlined onClick={() => remove(name)} />
                  </Space>
                ))}
                <Form.Item>
                  <Button
                    type="dashed"
                    onClick={() => add()}
                    block
                    icon={<PlusOutlined />}
                  >
                    Add field
                  </Button>
                </Form.Item>
              </>
            )}
          </Form.List>
        </Form.Item>

        {/*Input For Recipe Instructions */}
        <Form.Item label="Instructions">
          <Form.List name="instructions">
            {(subFields, subOpt) => (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  rowGap: 16,
                }}
              >
                {subFields.map((subField) => (
                  <Space key={subField.key}>
                    <Form.Item noStyle name={[subField.name, "instruction"]}>
                      <Input placeholder="Write Instructions" />
                    </Form.Item>
                    <CloseOutlined
                      onClick={() => {
                        subOpt.remove(subField.name);
                      }}
                    />
                  </Space>
                ))}
                <Button type="dashed" onClick={() => subOpt.add()}>
                  +
                </Button>
              </div>
            )}
          </Form.List>
        </Form.Item>

        {/*Input For Recipe Servings*/}
        <Form.Item
          label="Servings:"
          name="serving"
          rules={[
            { required: true, message: "Please input the Recipe Serving!" },
          ]}
        >
          <Input placeholder="#" className="antd-form-input" />
        </Form.Item>

        {/*Input For Recipe Cooking Time*/}
        <Form.Item
          label="Cooking Time:"
          name="cooking"
          className="cooking-time"
          rules={[
            {
              required: true,
              message: "Please input the Recipe Cooking time!",
            },
          ]}
        >
          <InputNumber
            placeholder="Hours 0"
            className="cooking-time-input antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe Prep Time*/}
        <Form.Item
          label="Prep Time:"
          name="prep"
          className="prep-time"
          rules={[
            {
              required: true,
              message: "Please input the Recipe Preparation time!",
            },
          ]}
        >
          <InputNumber
            placeholder="Hours 0"
            className="prep-time-input antd-form-input"
          />
        </Form.Item>

        {/*Input For Recipe cuisine*/}
