import React, { useState } from "react";
import { Select, Form, Input, Button, Upload, message } from "antd";
import { useNavigate } from "react-router-dom";
import { UploadOutlined, LoadingOutlined } from "@ant-design/icons";
const { Option } = Select;

function loadFromLocalStorage(key) {
  try {
    const item = localStorage.getItem(key);
    if (!item) {
      throw new Error("Item not found in local storage");
    }
    return JSON.parse(item);
  } catch (error) {
    console.error("Error retrieving item from local storage:", error);
  }
};
function saveToLocalStorage(key, value) {
  try {
    localStorage.setItem(key, JSON.stringify(value));
  } catch (error) {
    console.error("Error saving item to local storage:", error);
  }
};
function getAllRecepies() {
  return loadFromLocalStorage("recepies") || "[]";
};
function addRecepie(recepie) {
  let recepies = getAllRecepies();
  recepies.push(recepie);
  saveToLocalStorage("recepies", JSON.stringify(recepies));
}

function RecipeForm() {
  const [form] = Form.useForm();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [imageUrl, setImageUrl] = useState(null);

  const onFinish = (values) => {
    // Save the recipe to local storage
    const existingRecipes = JSON.parse(localStorage.getItem("recipes")) || [];
    const updatedRecipes = [...existingRecipes, { ...values, image: imageUrl }];
    localStorage.setItem("recipes", JSON.stringify(updatedRecipes));

    // Redirect to the RecipeListing page
    navigate("/recipe-Listing");
  };

  const getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
  };
  const normFile = (e) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };

  const beforeUpload = (file) => {
    const isImage = file.type.startsWith("image/");
    if (!isImage) {
      message.error("You can only upload image files!");
    }
    return isImage;
  };
  const handleChange = (info) => {
    if (info.file.status === "uploading") {
      setLoading(true);
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      getBase64(info.file.originFileObj, (url) => {
        setLoading(false);
        setImageUrl(url);
      });
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <UploadOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  function onSubmit(data) {
    console.log(data);
    let recepie = { ...data };
    recepie.ingredients.map((item) => item.first);
    addRecepie(recepie);

    console.log(getAllRecepies());
  }

  return (
    <div>
      <Form
        form={form}
        layout="vertical"
        requiredMark={false}
        onFinish={onFinish}
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
      >
        <Form.Item
          name="title"
          label="Recepie Title"
          rules={[{ required: true }]}
        >
          <Input placeholder="Enter Your Recipe name" />
        </Form.Item>
        <Form.Item
          label="Recipe Image"
          name="image"
          className="upload"
          valuePropName="fileList"
          getValueFromEvent={normFile}
          noStyle
        >
          <Upload
            name="avatar"
            listType="picture-card"
            className="avatar-uploader"
            showUploadList={false}
            action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
            beforeUpload={beforeUpload}
            onChange={handleChange}
          >
            {imageUrl ? (
              <img
                src={imageUrl}
                alt="avatar"
                style={{
                  width: "-webkit-fill-available",
                  height: "200px",
                  objectFit: "fill",
                }}
              />
            ) : (
              uploadButton
            )}
          </Upload>
        </Form.Item>

        <Form.Item label="Description" name="description">
          <Input.TextArea allowClear showCount />
        </Form.Item>

        <Form.Item label="Ingredients">
          <Form.List name="ingredients">
            {(subFields, subOpt) => (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  rowGap: 16,
                }}
              >
                {subFields.map((subField) => (
                  <Space key={subField.key}>
                    <Form.Item noStyle name={[subField.name, "first"]}>
                      <Input placeholder="Write Instructions" />
                    </Form.Item>
                    <CloseOutlined
                      onClick={() => {
                        subOpt.remove(subField.name);
                      }}
                    />
                  </Space>
                ))}
                <Button type="dashed" onClick={() => subOpt.add()}>
                  +
                </Button>
              </div>
            )}
          </Form.List>
        </Form.Item>
        <Form.Item name="serving" label="Serving" rules={[{ required: true }]}>
          <Input />
        </Form.Item>
        <Form.Item>
          <Space>
            {/* <SubmitButton form={form} /> */}
            <Button htmlType="submit">Submit</Button>
          </Space>
        </Form.Item>
      </Form>
    </div>
  );
}

export default RecipeForm;
